import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const EditPaper = ({ match }) => {
  const [content, setContent] = useState('');
  const { paperId } = match.params;

  useEffect(() => {
    const fetchPdfText = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/papers/text/${paperId}`);
        setContent(response.data.text);
      } catch (error) {
        console.error('Error fetching PDF text:', error);
      }
    };

    fetchPdfText();
  }, [paperId]);

  const handleSave = async () => {
    try {
      // Implement save functionality
      alert('Content saved successfully!');
    } catch (error) {
      console.error('Error saving content:', error);
      alert('Failed to save content.');
    }
  };

  const handleImport = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/papers/text/${paperId}`);
      setContent(response.data.text);
    } catch (error) {
      console.error('Error importing text:', error);
      alert('Failed to import text.');
    }
  };

  return (
    <div className="edit-paper-container">
      <h1>Edit Paper</h1>
      <ReactQuill value={content} onChange={setContent} style={{ height: '80vh' }} />
      <button onClick={handleSave} className="save-button">Save</button>
      <button onClick={handleImport} className="import-button">Import</button>
    </div>
  );
};

export default EditPaper;
