import React, { useState } from 'react';
import axios from 'axios';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './styles/AddPaperByEntering.css';

const AddPaperByEntering = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [author, setAuthor] = useState('');
  const [keywords, setKeywords] = useState('');
  const [error, setError] = useState(null);

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5001/api/papers', {
        title,
        content,
        author,
        keywords,
        permissions: 'private',
        libraryId: selectedLibrary
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Paper saved successfully!');
      setTitle('');
      setContent('');
      setAuthor('');
      setKeywords('');
      setError(null);
    } catch (error) {
      console.error('Error saving paper:', error);
      setError('Failed to save paper.');
    }
  };

  return (
    <div className="add-paper-container">
      <h1>Add Paper by Entering</h1>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
        className="title-input"
      />
      <input
        type="text"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        placeholder="Author"
        className="author-input"
      />
      <input
        type="text"
        value={keywords}
        onChange={(e) => setKeywords(e.target.value)}
        placeholder="Keywords (separated by space)"
        className="keywords-input"
      />
      <ReactQuill value={content} onChange={setContent} className="editor" />
      <button onClick={handleSave} className="save-button">Save</button>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default AddPaperByEntering;
