import React from 'react';
import { useHistory } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const history = useHistory();

  const handleLoginRegisterClick = () => {
    history.push('/auth'); // 跳转到登录或注册页面
  };

  const handleLogoutClick = () => {
    localStorage.removeItem('token'); // 清除存储在 localStorage 中的令牌
    alert('You have been logged out.');
    history.push('/auth'); // 跳转到登录或注册页面
  };

  return (
    <div className="home-container">
      <div className="home-content">
        <h1>Welcome to Paper Management System</h1>
        <button onClick={handleLoginRegisterClick} className="home-button">Login or Register</button>
        <button onClick={handleLogoutClick} className="logout-button">Logout</button>
      </div>
    </div>
  );
};

export default Home;
