import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './UpdateProfile.css'; // 使用独立的样式文件

const UpdateProfile = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const history = useHistory();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      history.push('/auth');
    }
  }, [history]);

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      alert('Passwords do not match!');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.put('http://localhost:5001/api/auth/update', { username, password }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Profile updated successfully!');
      history.push('/dashboard');
    } catch (error) {
      console.error('Profile update failed:', error.response?.data?.message || error.message);
      alert('Profile update failed! ' + (error.response?.data?.message || error.message));
    }
  };

  return (
    <div className="update-profile-container">
      <div className="update-profile-form">
        <h2>Update Profile</h2>
        <form onSubmit={handleUpdateProfile}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="New Username"
            required
            className="update-profile-input"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="New Password"
            required
            className="update-profile-input"
          />
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm Password"
            required
            className="update-profile-input"
          />
          <button type="submit" className="update-profile-button">Update Profile</button>
        </form>
      </div>
    </div>
  );
};

export default UpdateProfile;
