import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './MyLibraries.css';

const MyLibraries = () => {
  const history = useHistory();
  const [isEditorVisible, setIsEditorVisible] = useState(false);
  const [isLibraryVisible, setIsLibraryVisible] = useState(true);
  const [isAddLibraryVisible, setIsAddLibraryVisible] = useState(false);
  const [isSaveToLibraryVisible, setIsSaveToLibraryVisible] = useState(false);
  const [isEditInfoVisible, setIsEditInfoVisible] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [keywords, setKeywords] = useState('');
  const [pdfFile, setPdfFile] = useState(null);
  const [error, setError] = useState(null);
  const [papers, setPapers] = useState([]);
  const [libraries, setLibraries] = useState([]);
  const [selectedLibrary, setSelectedLibrary] = useState('');
  const [newLibraryName, setNewLibraryName] = useState('');
  const [showOptions, setShowOptions] = useState(null);
  const [showLibraryOptions, setShowLibraryOptions] = useState(null);
  const [showPrivacyOptions, setShowPrivacyOptions] = useState(null);
  const [isMoveVisible, setIsMoveVisible] = useState(false);
  const [selectedPaperId, setSelectedPaperId] = useState(null);
  const [libraryPrivacy, setLibraryPrivacy] = useState({});
  const [editingPaperId, setEditingPaperId] = useState(null);

  const optionsRef = useRef([]);
  const libraryOptionsRef = useRef([]);
  const privacyOptionsRef = useRef([]);

  useEffect(() => {
    const fetchLibraries = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5001/api/libraries/mylibraries', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setLibraries(response.data);
        const initialPrivacy = response.data.reduce((acc, library) => {
          acc[library.id] = library.permission || 'Public';
          return acc;
        }, {});
        setLibraryPrivacy(initialPrivacy);
      } catch (error) {
        console.error('Error fetching libraries:', error);
      }
    };

    fetchLibraries();

    const handleClickOutside = (event) => {
      if (!optionsRef.current.some(ref => ref && ref.contains(event.target))) {
        setShowOptions(null);
      }
      if (!libraryOptionsRef.current.some(ref => ref && ref.contains(event.target))) {
        setShowLibraryOptions(null);
      }
      if (!privacyOptionsRef.current.some(ref => ref && ref.contains(event.target))) {
        setShowPrivacyOptions(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleAddPaper = () => {
    clearInfoInputs(); // 清空文本框内容
    setIsEditorVisible(true);
    setIsLibraryVisible(false);
    setIsAddLibraryVisible(false);
    setIsSaveToLibraryVisible(false);
    setIsEditInfoVisible(false);
  };

  const handleViewLibraries = () => {
    setIsLibraryVisible(true);
    setIsEditorVisible(false);
    setIsSaveToLibraryVisible(false);
    setSelectedLibrary('');
    setPapers([]);
    clearInfoInputs(); // 清除信息输入框的内容
  };

  const handleSave = async () => {
    if (!selectedLibrary) {
      alert('Please select a library');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('keywords', keywords);
    formData.append('permissions', 'private');
    formData.append('libraryId', selectedLibrary);
    if (pdfFile) {
      formData.append('pdf', pdfFile);
    }

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5001/api/papers', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Paper saved successfully!');
      setTitle('');
      setAuthor('');
      setKeywords('');
      setPdfFile(null);
      setError(null);
      setIsSaveToLibraryVisible(false);
      setIsEditorVisible(true);
    } catch (error) {
      console.error('Error saving paper:', error);
      setError('Failed to save paper.');
    }
  };

  const handlePdfChange = (e) => {
    setPdfFile(e.target.files[0]);
  };

  const handleAddLibrary = async () => {
    if (!newLibraryName) {
      setError('Library name cannot be empty.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5001/api/libraries', {
        name: newLibraryName
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Library created successfully!');
      setIsAddLibraryVisible(false);
      setNewLibraryName('');
      setError(null);
      const response = await axios.get('http://localhost:5001/api/libraries/mylibraries', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setLibraries(response.data);
    } catch (error) {
      console.error('Error creating library:', error);
      setError('Failed to create library.');
    }
  };

  const handleViewLibrary = async (libraryId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5001/api/libraries/${libraryId}/papers`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setPapers(response.data);
      setSelectedLibrary(libraryId);
      setIsLibraryVisible(false);
      setIsEditorVisible(false);
      setIsAddLibraryVisible(false);
    } catch (error) {
      console.error('Error fetching papers:', error);
    }
  };

  const handleSaveToLibrary = (libraryId) => {
    setSelectedLibrary(libraryId);
  };

  const handleBackToLibraries = () => {
    setIsLibraryVisible(true);
    setPapers([]);
    setSelectedLibrary('');
    setIsEditInfoVisible(false); // 确保信息页面内容消失
    clearInfoInputs(); // 清除信息输入框的内容
  };

  const handleDeletePaper = async (paperId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/papers/${paperId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Paper deleted successfully!');
      handleViewLibrary(selectedLibrary);
    } catch (error) {
      console.error('Error deleting paper:', error);
      setError('Failed to delete paper.');
    }
  };

  const handleDeleteLibrary = async (libraryId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/libraries/${libraryId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Library deleted successfully!');
      const response = await axios.get('http://localhost:5001/api/libraries/mylibraries', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setLibraries(response.data);
      setSelectedLibrary('');
      setPapers([]);
      setIsLibraryVisible(true);
    } catch (error) {
      console.error('Error deleting library:', error);
      setError('Failed to delete library.');
    }
  };

  const handleMovePaper = (paperId) => {
    setSelectedPaperId(paperId);
    setIsMoveVisible(true);
  };

  const handleConfirmMove = async (targetLibraryId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/papers/${selectedPaperId}/move`, { libraryId: targetLibraryId }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Paper moved successfully!');
      setIsMoveVisible(false);
      handleViewLibrary(selectedLibrary);
    } catch (error) {
      console.error('Error moving paper:', error);
      setError('Failed to move paper.');
    }
  };

  const handleEditPaper = (paperId) => {
    window.open(`http://localhost:3000/edit-paper/${paperId}`, '_blank');
  };

  const handleEditInfo = async (paperId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5001/api/papers/${paperId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setTitle(response.data.title);
      setAuthor(response.data.author);
      setKeywords(response.data.keywords);
      setEditingPaperId(paperId);
      setIsEditInfoVisible(true);
      setIsLibraryVisible(false);
      setIsEditorVisible(false);
      setIsSaveToLibraryVisible(false);
    } catch (error) {
      console.error('Error fetching paper details:', error);
      setError('Failed to fetch paper details.');
    }
  };

  const handleUpdatePaper = async () => {
    if (!title || !author || !keywords) {
      setError('All fields are required.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/papers/${editingPaperId}`, {
        title,
        author,
        keywords
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Paper updated successfully!');
      setIsEditInfoVisible(false);
      handleViewLibrary(selectedLibrary);
    } catch (error) {
      console.error('Error updating paper:', error);
      setError('Failed to update paper.');
    }
  };

  const clearInfoInputs = () => {
    setTitle('');
    setAuthor('');
    setKeywords('');
  };

  const toggleOptions = (paperId) => {
    setShowOptions(showOptions === paperId ? null : paperId);
  };

  const toggleLibraryOptions = (libraryId) => {
    setShowLibraryOptions(showLibraryOptions === libraryId ? null : libraryId);
  };

  const togglePrivacyOptions = (libraryId) => {
    setShowPrivacyOptions(showPrivacyOptions === libraryId ? null : libraryId);
  };

  const handlePrivacyChange = async (libraryId, privacy) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/libraries/${libraryId}/privacy`, {
        privacy
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setLibraryPrivacy((prevPrivacy) => ({
        ...prevPrivacy,
        [libraryId]: privacy
      }));
      alert('Privacy updated successfully!');
    } catch (error) {
      console.error('Error updating privacy:', error);
      setError('Failed to update privacy.');
    }
  };

  return (
    <div className="mylibraries-container">
      {isLibraryVisible && (
        <>
          <h1>My Libraries</h1>
          <button onClick={handleAddPaper} className="add-button">Add Paper</button>
          <button onClick={() => setIsAddLibraryVisible(true)} className="add-button">Add Library</button>
          <div className="mylibraries-list">
            {libraries.map((library, index) => (
              <div key={library.id} className="mylibraries-item">
                <div className="mylibraries-item-content">
                  <h3>{library.name}</h3>
                  <p><strong>Owner:</strong> user{library.userId}</p>
                  <div className="library-actions">
                    <button onClick={() => handleViewLibrary(library.id)} className="view-library-button">View Library</button>
                    <div className="library-options-container" ref={el => libraryOptionsRef.current[index] = el}>
                      <button className="library-options-button" onClick={() => toggleLibraryOptions(library.id)}>Options</button>
                      {showLibraryOptions === library.id && (
                        <div className="library-options-dropdown">
                          <button onClick={() => handleDeleteLibrary(library.id)}>Delete Library</button>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
                <div className="library-privacy-container" ref={el => privacyOptionsRef.current[index] = el}>
                  <button className="library-privacy-button" onClick={() => togglePrivacyOptions(library.id)}>
                    {libraryPrivacy[library.id]}
                  </button>
                  {showPrivacyOptions === library.id && (
                    <div className="library-privacy-options-dropdown">
                      <button onClick={() => handlePrivacyChange(library.id, 'Public')}>Public</button>
                      <button onClick={() => handlePrivacyChange(library.id, 'Private')}>Private</button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </>
      )}

      {isEditorVisible && (
        <div className="editor-container">
          <h2>Upload Paper</h2>
          <div className="form-group">
            <label htmlFor="title">Title:</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="author">Author:</label>
            <input
              type="text"
              id="author"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="keywords">Keywords:</label>
            <input
              type="text"
              id="keywords"
              value={keywords}
              onChange={(e) => setKeywords(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="pdf">PDF:</label>
            <input
              type="file"
              id="pdf"
              onChange={handlePdfChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="library">Save to Library:</label>
            <select
              id="library"
              value={selectedLibrary}
              onChange={(e) => handleSaveToLibrary(e.target.value)}
            >
              <option value="">Select Library</option>
              {libraries.map((library) => (
                <option key={library.id} value={library.id}>
                  {library.name}
                </option>
              ))}
            </select>
          </div>
          <button onClick={handleSave} className="save-button">Save</button>
          <button onClick={handleViewLibraries} className="cancel-button">Cancel</button>
        </div>
      )}

      {isAddLibraryVisible && (
        <div className="add-library-container">
          <h2>Add Library</h2>
          <div className="form-group">
            <label htmlFor="newLibraryName">Library Name:</label>
            <input
              type="text"
              id="newLibraryName"
              value={newLibraryName}
              onChange={(e) => setNewLibraryName(e.target.value)}
            />
          </div>
          <button onClick={handleAddLibrary} className="add-library-button">Add Library</button>
          <button onClick={handleViewLibraries} className="cancel-button">Cancel</button>
        </div>
      )}

      {isSaveToLibraryVisible && (
        <div className="save-to-library-container">
          <h2>Save Paper to Library</h2>
          <div className="form-group">
            <label htmlFor="library">Library:</label>
            <select
              id="library"
              value={selectedLibrary}
              onChange={(e) => handleSaveToLibrary(e.target.value)}
            >
              <option value="">Select Library</option>
              {libraries.map((library) => (
                <option key={library.id} value={library.id}>
                  {library.name}
                </option>
              ))}
            </select>
          </div>
          <button onClick={handleSave} className="save-button">Save</button>
          <button onClick={handleViewLibraries} className="cancel-button">Cancel</button>
        </div>
      )}

      {!isLibraryVisible && !isEditorVisible && !isAddLibraryVisible && !isSaveToLibraryVisible && (
        <div className="all-papers-container">
          <div className="back-button-container">
            <button className="back-button" onClick={handleBackToLibraries}>Back to Libraries</button>
          </div>
          <h2>Library Papers</h2>
          <div className="all-papers-list">
            {papers.map((paper, index) => (
              <div key={paper.id} className="all-papers-item">
                <div className="all-papers-paper-content">
                  <h2>{paper.title}</h2>
                  {paper.author && <p><strong>Author:</strong> {paper.author}</p>}
                  {paper.keywords && <p><strong>Keywords:</strong> {paper.keywords}</p>}
                </div>
                <div className="all-papers-options-container" ref={el => optionsRef.current[index] = el}>
                  <button className="options-button" onClick={() => toggleOptions(paper.id)}>Options</button>
                  {showOptions === paper.id && (
                    <div className="all-papers-options-dropdown">
                      <button className="view-paper-button" onClick={() => window.open(`http://localhost:5001/api/papers/pdf/${paper.id}`, '_blank')}>View Paper</button>
                      <button onClick={() => handleEditPaper(paper.id)}>Edit Paper</button>
                      <button onClick={() => handleMovePaper(paper.id)}>Move Paper</button>
                      <button onClick={() => handleDeletePaper(paper.id)}>Delete Paper</button>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {isMoveVisible && (
        <div className="move-paper-container">
          <h2>Move Paper</h2>
          <div className="form-group">
            <label htmlFor="targetLibrary">Select Target Library:</label>
            <select
              id="targetLibrary"
              onChange={(e) => handleConfirmMove(e.target.value)}
            >
              <option value="">Select Library</option>
              {libraries.map((library) => (
                <option key={library.id} value={library.id}>
                  {library.name}
                </option>
              ))}
            </select>
          </div>
          <button onClick={() => setIsMoveVisible(false)} className="cancel-button">Cancel</button>
        </div>
      )}

      {isEditInfoVisible && (
        <div className="edit-info-container">
          <h2>Edit Paper Info</h2>
          <div className="form-group">
            <label htmlFor="title">Title:</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="author">Author:</label>
            <input
              type="text"
              id="author"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="keywords">Keywords:</label>
            <input
              type="text"
              id="keywords"
              value={keywords}
              onChange={(e) => setKeywords(e.target.value)}
            />
          </div>
          <button onClick={handleUpdatePaper} className="save-button">Save</button>
          <button onClick={handleBackToLibraries} className="cancel-button">Cancel</button>
        </div>
      )}
    </div>
  );
};

export default MyLibraries;
