import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './MyLibraries.css';

const MyLibraries = () => {
  const history = useHistory();
  const [isEditorVisible, setIsEditorVisible] = useState(false);
  const [isLibraryVisible, setIsLibraryVisible] = useState(true);
  const [isAddLibraryVisible, setIsAddLibraryVisible] = useState(false);
  const [isSaveToLibraryVisible, setIsSaveToLibraryVisible] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [keywords, setKeywords] = useState('');
  const [pdfFile, setPdfFile] = useState(null);
  const [error, setError] = useState(null);
  const [papers, setPapers] = useState([]);
  const [libraries, setLibraries] = useState([]);
  const [selectedLibrary, setSelectedLibrary] = useState('');
  const [newLibraryName, setNewLibraryName] = useState('');
  const [showOptions, setShowOptions] = useState(null);
  const [showLibraryOptions, setShowLibraryOptions] = useState(null);
  const [isMoveVisible, setIsMoveVisible] = useState(false);
  const [selectedPaperId, setSelectedPaperId] = useState(null);
  const [libraryPrivacy, setLibraryPrivacy] = useState({});

  const optionsRef = useRef([]);
  const libraryOptionsRef = useRef([]);

  useEffect(() => {
    const fetchLibraries = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5001/api/libraries', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setLibraries(response.data);
        const initialPrivacy = response.data.reduce((acc, library) => {
          acc[library.id] = library.permission || 'Public'; // 使用数据库中的权限值
          return acc;
        }, {});
        setLibraryPrivacy(initialPrivacy);
      } catch (error) {
        console.error('Error fetching libraries:', error);
      }
    };

    fetchLibraries();

    const handleClickOutside = (event) => {
      if (!optionsRef.current.some(ref => ref && ref.contains(event.target))) {
        setShowOptions(null);
      }
      if (!libraryOptionsRef.current.some(ref => ref && ref.contains(event.target))) {
        setShowLibraryOptions(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleAddPaper = () => {
    setIsEditorVisible(true);
    setIsLibraryVisible(false);
    setIsAddLibraryVisible(false);
    setIsSaveToLibraryVisible(false);
  };

  const handleViewLibraries = () => {
    setIsLibraryVisible(true);
    setIsEditorVisible(false);
    setIsSaveToLibraryVisible(false);
    setSelectedLibrary('');
    setPapers([]);
  };

  const handleSave = async () => {
    if (!selectedLibrary) {
      alert('Please select a library');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('keywords', keywords);
    formData.append('permissions', 'private');
    formData.append('libraryId', selectedLibrary);
    if (pdfFile) {
      formData.append('pdf', pdfFile);
    }

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5001/api/papers', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Paper saved successfully!');
      setTitle('');
      setAuthor('');
      setKeywords('');
      setPdfFile(null);
      setError(null);
      setIsSaveToLibraryVisible(false);
      setIsEditorVisible(true);
    } catch (error) {
      console.error('Error saving paper:', error);
      setError('Failed to save paper.');
    }
  };

  const handlePdfChange = (e) => {
    setPdfFile(e.target.files[0]);
  };

  const handleAddLibrary = async () => {
    if (!newLibraryName) {
      setError('Library name cannot be empty.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5001/api/libraries', {
        name: newLibraryName
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Library created successfully!');
      setIsAddLibraryVisible(false);
      setNewLibraryName('');
      setError(null);
      const response = await axios.get('http://localhost:5001/api/libraries', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setLibraries(response.data);
    } catch (error) {
      console.error('Error creating library:', error);
      setError('Failed to create library.');
    }
  };

  const handleViewLibrary = async (libraryId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5001/api/libraries/${libraryId}/papers`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setPapers(response.data);
      setSelectedLibrary(libraryId);
      setIsLibraryVisible(false);
      setIsEditorVisible(false);
      setIsAddLibraryVisible(false);
    } catch (error) {
      console.error('Error fetching papers:', error);
    }
  };

  const handleSaveToLibrary = (libraryId) => {
    setSelectedLibrary(libraryId);
  };

  const handleBackToLibraries = () => {
    setIsLibraryVisible(true);
    setPapers([]);
    setSelectedLibrary('');
  };

  const handleDeletePaper = async (paperId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/papers/${paperId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Paper deleted successfully!');
      handleViewLibrary(selectedLibrary);
    } catch (error) {
      console.error('Error deleting paper:', error);
      setError('Failed to delete paper.');
    }
  };

  const handleDeleteLibrary = async (libraryId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5001/api/libraries/${libraryId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Library deleted successfully!');
      const response = await axios.get('http://localhost:5001/api/libraries', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setLibraries(response.data);
      setSelectedLibrary('');
      setPapers([]);
      setIsLibraryVisible(true);
    } catch (error) {
      console.error('Error deleting library:', error);
      setError('Failed to delete library.');
    }
  };

  const handleMovePaper = (paperId) => {
    setSelectedPaperId(paperId);
    setIsMoveVisible(true);
  };

  const handleConfirmMove = async (targetLibraryId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/papers/${selectedPaperId}/move`, { libraryId: targetLibraryId }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Paper moved successfully!');
      setIsMoveVisible(false);
      handleViewLibrary(selectedLibrary);
    } catch (error) {
      console.error('Error moving paper:', error);
      setError('Failed to move paper.');
    }
  };

  const handleEditPaper = (paperId) => {
    window.open(`http://localhost:3000/edit-paper/${paperId}`, '_blank');
  };

  const toggleOptions = (paperId) => {
    setShowOptions(showOptions === paperId ? null : paperId);
  };

  const toggleLibraryOptions = (libraryId, event) => {
    event.stopPropagation(); // 阻止事件冒泡到 library-item 的 onClick 事件
    setShowLibraryOptions(showLibraryOptions === libraryId ? null : libraryId);
  };

  const handleLibraryPrivacyChange = async (libraryId, newPrivacy) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/libraries/${libraryId}`, { permission: newPrivacy }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setLibraryPrivacy({ ...libraryPrivacy, [libraryId]: newPrivacy });
      setShowLibraryOptions(null);
    } catch (error) {
      console.error('Error updating library privacy:', error);
      alert('Failed to update library privacy.');
    }
  };

  return (
    <div className="libraries-container">
      <h1>My Libraries</h1>
      <div className="button-container">
        <button className="add-paper-button" onClick={handleAddPaper}>
          Add Paper
        </button>
        <button className="view-libraries-button" onClick={handleViewLibraries}>
          View Libraries
        </button>
      </div>
      {isLibraryVisible && (
        <div className="libraries-list">
          {libraries.map((library, index) => (
            <div
              key={library.id}
              className={`library-item ${selectedLibrary === library.id ? 'selected' : ''}`}
              style={{ color: 'black' }}
              onClick={() => handleViewLibrary(library.id)}
            >
              <div className="library-content">
                <h3>{library.name}</h3>
              </div>
              <div className="options-container" ref={el => libraryOptionsRef.current[index] = el}>
                <button className="privacy-button" onClick={(event) => toggleLibraryOptions(library.id, event)}>
                  {libraryPrivacy[library.id] || 'Public'}
                </button>
                <button className="options-button" onClick={(event) => toggleLibraryOptions(library.id, event)}>Options</button>
                {showLibraryOptions === library.id && (
                  <div className="options-dropdown">
                    <button className="privacy-option" onClick={() => handleLibraryPrivacyChange(library.id, 'Private')}>Private</button>
                    <button className="privacy-option" onClick={() => handleLibraryPrivacyChange(library.id, 'Public')}>Public</button>
                    <button className="privacy-option" onClick={() => handleLibraryPrivacyChange(library.id, 'Shared')}>Shared</button>
                  </div>
                )}
              </div>
            </div>
          ))}
          <button className="add-library-button" onClick={() => setIsAddLibraryVisible(true)}>
            Add Library
          </button>
        </div>
      )}
      {isAddLibraryVisible && (
        <div className="add-library-form">
          <input
            type="text"
            value={newLibraryName}
            onChange={(e) => setNewLibraryName(e.target.value)}
            placeholder="Library Name"
          />
          <button onClick={handleAddLibrary} className="save-library-button">Save Library</button>
          {error && <p className="error">{error}</p>}
        </div>
      )}
      {isEditorVisible && (
        <div className="editor-container">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Title"
            className="title-input"
          />
          <input
            type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            placeholder="Author"
            className="author-input"
          />
          <input
            type="text"
            value={keywords}
            onChange={(e) => setKeywords(e.target.value)}
            placeholder="Keywords (separated by space)"
            className="keywords-input"
          />
          <input
            type="file"
            accept="application/pdf"
            onChange={handlePdfChange}
            className="pdf-input"
          />
          <button onClick={() => setIsSaveToLibraryVisible(true)} className="save-button">Save</button>
          {error && <p className="error">{error}</p>}
        </div>
      )}
      {isSaveToLibraryVisible && (
        <div className="libraries-list">
          <p>Select a library to save the paper:</p>
          {libraries.map((library) => (
            <div key={library.id} className={`library-item ${selectedLibrary === library.id ? 'selected' : ''} ${selectedLibrary === library.id ? 'opaque' : ''}`} onClick={() => handleSaveToLibrary(library.id)} style={{ color: 'black' }}>
              <h3>{library.name}</h3>
            </div>
          ))}
          <button onClick={() => { handleSave(); setIsSaveToLibraryVisible(false); }} className="save-button">Confirm Save</button>
        </div>
      )}
      {isMoveVisible && (
        <div className="libraries-list">
          <p>Select a library to move the paper to:</p>
          {libraries
            .filter(library => library.id !== selectedLibrary)
            .map((library) => (
              <div key={library.id} className={`library-item`} onClick={() => handleConfirmMove(library.id)} style={{ color: 'black' }}>
                <h3>{library.name}</h3>
              </div>
            ))}
          <button onClick={() => setIsMoveVisible(false)} className="cancel-move-button">Cancel</button>
        </div>
      )}
      {selectedLibrary && !isEditorVisible && !isLibraryVisible && !isMoveVisible && (
        <div className="library-papers-container">
          {papers.length === 0 ? (
            <p className="empty-message">Empty</p>
          ) : (
            <div className="papers-list">
              {papers.map((paper, index) => (
                <div key={paper.id} className="paper-item">
                  <div className="paper-content">
                    <h2>{paper.title}</h2>
                    {paper.author && <p><strong>Author:</strong> {paper.author}</p>}
                    {paper.keywords && <p><strong>Keywords:</strong> {paper.keywords}</p>}
                  </div>
                  <div className="options-container" ref={el => optionsRef.current[index] = el}>
                    <button className="options-button" onClick={() => toggleOptions(paper.id)}>Options</button>
                    {showOptions === paper.id && (
                      <div className="options-dropdown">
                        <button className="view-paper-button" onClick={() => window.open(`http://localhost:5001/api/papers/pdf/${paper.id}`, '_blank')}>View Paper</button>
                        <button className="move-paper-button" onClick={() => handleMovePaper(paper.id)}>Move</button>
                        <button className="edit-paper-button" onClick={() => handleEditPaper(paper.id)}>Edit</button>
                        <button className="delete-paper-button" onClick={() => handleDeletePaper(paper.id)}>Delete</button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
          <button onClick={handleBackToLibraries} className="back-to-libraries-button">Back to Libraries</button>
        </div>
      )}
    </div>
  );
};

export default MyLibraries;
