import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import Quill from 'quill';
import 'quill/dist/quill.snow.css';
import './EditPaper.css';

const EditPaper = ({ match }) => {
  const { paperId } = match.params;
  const editorRef = useRef(null);
  const [editor, setEditor] = useState(null);

  useEffect(() => {
    if (editorRef.current) {
      const quillEditor = new Quill(editorRef.current, {
        theme: 'snow',
        modules: {
          toolbar: [
            [{ 'header': '1'}, { 'header': '2'}, { 'font': [] }],
            [{ 'list': 'ordered'}, { 'list': 'bullet' }],
            ['bold', 'italic', 'underline'],
            ['image', 'code-block']
          ]
        }
      });
      setEditor(quillEditor);

      // Load PDF text
      axios.get(`http://localhost:5001/api/papers/text/${paperId}`)
        .then(response => {
          quillEditor.setText(response.data.text);
        })
        .catch(error => {
          console.error('Error loading paper text:', error);
        });
    }
  }, [editorRef, paperId]);

  return (
    <div className="edit-paper-container">
      <div ref={editorRef} className="quill-editor"></div>
    </div>
  );
};

export default EditPaper;
