import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AllPapers.css';

const AllPapers = () => {
  const [papers, setPapers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchPapers = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/papers');
        setPapers(response.data);
      } catch (error) {
        console.error('Error fetching papers:', error);
      }
    };

    fetchPapers();
  }, []);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/papers/search?search=${searchTerm}`);
      setPapers(response.data);
    } catch (error) {
      console.error('Error searching papers:', error);
    }
  };

  return (
    <div className="all-papers-container">
      <h1>All Papers</h1>
      <div className="search-container">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search papers"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="papers-list">
        {papers.map((paper) => (
          <div key={paper.id} className="paper-item">
            <h2>{paper.title}</h2>
            {paper.author && <p>Author: {paper.author}</p>}
            {paper.keywords && <p>Keywords: {paper.keywords}</p>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllPapers;
