import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './AllLibraries.css';

const AllLibraries = () => {
  const [libraries, setLibraries] = useState([]);
  const [selectedLibrary, setSelectedLibrary] = useState(null);
  const [papers, setPapers] = useState([]);
  const [showOptions, setShowOptions] = useState(null);
  const optionsRef = useRef([]);

  useEffect(() => {
    const fetchLibraries = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5001/api/libraries', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setLibraries(response.data);
      } catch (error) {
        console.error('Error fetching libraries:', error);
      }
    };

    fetchLibraries();

    const handleClickOutside = (event) => {
      if (!optionsRef.current.some(ref => ref && ref.contains(event.target))) {
        setShowOptions(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleViewLibrary = async (libraryId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5001/api/libraries/${libraryId}/papers`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setPapers(response.data);
      setSelectedLibrary(libraryId);
    } catch (error) {
      console.error('Error fetching papers:', error);
    }
  };

  const toggleOptions = (paperId) => {
    setShowOptions(showOptions === paperId ? null : paperId);
  };

  return (
    <div className="all-libraries-container">
      <h1>All Libraries</h1>
      <div className="all-libraries-list">
        {libraries.map((library) => (
          <div key={library.id} className="all-libraries-item">
            <div className="all-libraries-content">
              <h3>{library.name}</h3>
            </div>
            <button className="all-libraries-view-button" onClick={() => handleViewLibrary(library.id)}>View</button>
          </div>
        ))}
      </div>

      {selectedLibrary && (
        <div className="all-libraries-papers-container">
          {papers.length === 0 ? (
            <p className="all-libraries-empty-message">No papers found</p>
          ) : (
            <div className="all-libraries-papers-list">
              {papers.map((paper, index) => (
                <div key={paper.id} className="all-libraries-paper-item">
                  <div className="all-libraries-paper-content">
                    <h2>{paper.title}</h2>
                    {paper.author && <p><strong>Author:</strong> {paper.author}</p>}
                    {paper.keywords && <p><strong>Keywords:</strong> {paper.keywords}</p>}
                  </div>
                  <div className="all-libraries-options-container" ref={el => optionsRef.current[index] = el}>
                    <button className="all-libraries-options-button" onClick={() => toggleOptions(paper.id)}>Options</button>
                    {showOptions === paper.id && (
                      <div className="all-libraries-options-dropdown">
                        <button className="all-libraries-view-paper-button" onClick={() => window.open(`http://localhost:5001/api/papers/pdf/${paper.id}`, '_blank')}>View Paper</button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default AllLibraries;
