import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './MyLibraries.css';

const MyLibraries = () => {
  const history = useHistory();
  const [isEditorVisible, setIsEditorVisible] = useState(false);
  const [isLibraryVisible, setIsLibraryVisible] = useState(false);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState(null);
  const [papers, setPapers] = useState([]);
  const [libraries, setLibraries] = useState([]);
  const [selectedLibrary, setSelectedLibrary] = useState('');

  useEffect(() => {
    const fetchPapers = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5001/api/papers/mylibrary', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setPapers(response.data);
      } catch (error) {
        console.error('Error fetching papers:', error);
      }
    };

    const fetchLibraries = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5001/api/libraries', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setLibraries(response.data);
      } catch (error) {
        console.error('Error fetching libraries:', error);
      }
    };

    fetchPapers();
    fetchLibraries();
  }, []);

  const handleAddPaper = () => {
    setIsEditorVisible(true);
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5001/api/papers', {
        title,
        content,
        permissions: 'private',
        libraryId: selectedLibrary
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Paper saved successfully!');
      setIsEditorVisible(false);
      setTitle('');
      setContent('');
      setError(null); // 清除错误状态
      // 刷新文献库列表
      const response = await axios.get('http://localhost:5001/api/papers/mylibrary', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setPapers(response.data);
    } catch (error) {
      console.error('Error saving paper:', error);
      setError('Failed to save paper.');
    }
  };

  const handleViewLibraries = () => {
    setIsLibraryVisible(true);
  };

  return (
    <div className="libraries-container">
      <h1>My Libraries</h1>
      {/* 文献库列表 */}
      <div className="papers-list">
        {papers.map((paper) => (
          <div key={paper.id} className="paper-item">
            <h2>{paper.title}</h2>
            <p>{paper.content}</p>
          </div>
        ))}
      </div>
      <button className="add-paper-button" onClick={handleAddPaper}>
        Add Paper
      </button>
      <button className="view-libraries-button" onClick={handleViewLibraries}>
        View Libraries
      </button>
      {isLibraryVisible && (
        <div className="libraries-list">
          {libraries.map((library) => (
            <div key={library.id} className="library-item" onClick={() => setSelectedLibrary(library.id)}>
              <h3>{library.name}</h3>
            </div>
          ))}
        </div>
      )}
      {isEditorVisible && (
        <div className="editor-container">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Title"
            className="title-input"
          />
          <ReactQuill value={content} onChange={setContent} className="editor" />
          <button onClick={handleSave} className="save-button">Save</button>
          {error && <p className="error">{error}</p>}
        </div>
      )}
    </div>
  );
};

export default MyLibraries;
