import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './AllPapers.css';

const AllPapers = () => {
  const [papers, setPapers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPaper, setSelectedPaper] = useState(null);
  const wrapperRef = useRef(null);

  useEffect(() => {
    const fetchPapers = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5001/api/papers/all', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setPapers(response.data);
      } catch (error) {
        console.error('Error fetching papers:', error);
      }
    };

    fetchPapers();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        setSelectedPaper(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [wrapperRef]);

  const handleSearch = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5001/api/papers/search?q=${searchQuery}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setPapers(response.data);
    } catch (error) {
      console.error('Error searching papers:', error);
    }
  };

  return (
    <div className="all-papers-container">
      <h1>All Papers</h1>
      <input
        type="text"
        placeholder="Search papers..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="search-input"
      />
      <button onClick={handleSearch} className="search-button">Search</button>
      <div className="papers-list">
        {papers.map((paper) => (
          <div
            key={paper.id}
            className="paper-item"
            onClick={() => setSelectedPaper(paper)}
            ref={wrapperRef}
          >
            <h3>{paper.title}</h3>
            <p>{paper.author}</p>
          </div>
        ))}
      </div>
      {selectedPaper && (
        <div className="paper-detail">
          <h2>{selectedPaper.title}</h2>
          <p>{selectedPaper.content}</p>
          <button onClick={() => setSelectedPaper(null)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default AllPapers;
