import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './MyLibraries.css';

const MyLibraries = () => {
  const history = useHistory();
  const [isEditorVisible, setIsEditorVisible] = useState(false);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState(null);

  const handleAddPaper = () => {
    setIsEditorVisible(true);
  };

  const handleSave = async () => {
    try {
      const response = await axios.post('http://localhost:5001/api/papers', {
        title,
        content,
        author: 'current_user', // 在实际应用中应从当前用户状态中获取
        permissions: 'private'
      });
      alert('Paper saved successfully!');
      setIsEditorVisible(false);
      setTitle('');
      setContent('');
      setError(null); // 清除错误状态
      // 你可以选择性地刷新文献库列表或跳转到其他页面
    } catch (error) {
      console.error('Error saving paper:', error);
      setError('Failed to save paper.');
    }
  };

  return (
    <div className="libraries-container">
      <h1>My Libraries</h1>
      {/* 这里可以添加文献库的列表和操作 */}
      <button className="add-paper-button" onClick={handleAddPaper}>
        Add Paper
      </button>
      {isEditorVisible && (
        <div className="editor-container">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Title"
            className="title-input"
          />
          <ReactQuill value={content} onChange={setContent} className="editor" />
          <button onClick={handleSave} className="save-button">Save</button>
          {error && <p className="error">{error}</p>}
        </div>
      )}
    </div>
  );
};

export default MyLibraries;
